import{_ as s,c as n,o as a,a as e}from"./app.bff02bbe.js";const m=JSON.parse('{"title":"MySQL 的一些查询小方法","description":"","frontmatter":{},"headers":[],"relativePath":"mysql/mysql-query.md","lastUpdated":null}'),l={name:"mysql/mysql-query.md"},p=e(`<h1 id="mysql-的一些查询小方法" tabindex="-1">MySQL 的一些查询小方法 <a class="header-anchor" href="#mysql-的一些查询小方法" aria-hidden="true">#</a></h1><br><hr><h5 id="_1、mysql-查询字段字数满足某个值的数据-char-length-函数的使用" tabindex="-1">1、MySQL 查询字段字数满足某个值的数据 （CHAR_LENGTH 函数的使用） <a class="header-anchor" href="#_1、mysql-查询字段字数满足某个值的数据-char-length-函数的使用" aria-hidden="true">#</a></h5><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">例：查询title字数等于3的所有数据，也配合使用大小于符号</span></span>
<span class="line"><span style="color:#A6ACCD;">    SELECT * FROM \`table\` WHERE CHAR_LENGTH(\`title\`)=1</span></span>
<span class="line"><span style="color:#A6ACCD;">注：CHAR_LENGTH 函数与 LENGTH 函数的区别是前者自动把3字节的中文当成1个字符</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h5 id="_2、正则表达式-regexp-的使用" tabindex="-1">2、正则表达式 （REGEXP 的使用） <a class="header-anchor" href="#_2、正则表达式-regexp-的使用" aria-hidden="true">#</a></h5><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">例：匹配所有字母</span></span>
<span class="line"><span style="color:#A6ACCD;">    SELECT * FROM \`one\` WHERE \`title\` REGEXP &#39;[a-zA-Z]+&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">注：MySQL并不能支持所有正则特性，比如元字符\\d、\\D等</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h5 id="_3、排序时指定某值在前面" tabindex="-1">3、排序时指定某值在前面 <a class="header-anchor" href="#_3、排序时指定某值在前面" aria-hidden="true">#</a></h5><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">例：表中数据status值有1，2，3，4。现在希望排序为 2，1，4，3</span></span>
<span class="line"><span style="color:#A6ACCD;">    SELECT</span></span>
<span class="line"><span style="color:#A6ACCD;">	*</span></span>
<span class="line"><span style="color:#A6ACCD;">    FROM</span></span>
<span class="line"><span style="color:#A6ACCD;">	yii_goddess_real</span></span>
<span class="line"><span style="color:#A6ACCD;">    ORDER BY</span></span>
<span class="line"><span style="color:#A6ACCD;">    (</span></span>
<span class="line"><span style="color:#A6ACCD;">        CASE</span></span>
<span class="line"><span style="color:#A6ACCD;">            WHEN \`status\` = 2 THEN 1</span></span>
<span class="line"><span style="color:#A6ACCD;">            WHEN \`status\` = 1 THEN 2</span></span>
<span class="line"><span style="color:#A6ACCD;">            WHEN \`status\` = 4 THEN 3</span></span>
<span class="line"><span style="color:#A6ACCD;">            ELSE 4</span></span>
<span class="line"><span style="color:#A6ACCD;">        END</span></span>
<span class="line"><span style="color:#A6ACCD;">    )</span></span>
<span class="line"><span style="color:#A6ACCD;">注：else作用是，如果有其他值，则指定排序值为4，排在最后</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br></div></div>`,9),r=[p];function c(i,t,o,d,b,C){return a(),n("div",null,r)}const A=s(l,[["render",c]]);export{m as __pageData,A as default};
